import React, { useState } from "react";
import "./VendorList.css";
import BookingForm from "./BookingForm"; // Import the Booking Form component

function VendorList({ vendors }) {
    const [selectedVendor, setSelectedVendor] = useState(null);

    const handleBookingClick = (vendor) => {
        setSelectedVendor(vendor);
    };

    return (
        <div className="vendor-list">
            <h2>Search Results</h2>
            <div className="vendor-list-container">
                {vendors.map((vendor) => (
                    <div className="vendor-card" key={vendor.id}>
                        <h3>{vendor.name}</h3>
                        <p>Category: {vendor.category}</p>
                        <p>Location: {vendor.location}</p>
                        <p>Rating: ‚≠ê {vendor.rating}</p>
                        <p>Services: {vendor.services.join(", ")}</p>
                        <p>Rate Per Event: {vendor.ratePerEvent}</p>
                        <p>Contact: {vendor.contact}</p>
                        <button className="book-button" onClick={() => handleBookingClick(vendor)}>üìÖ Book Now</button>
                    </div>
                ))}
            </div>

            {selectedVendor && <BookingForm vendor={selectedVendor} />}
        </div>
    );
}

export default VendorList;
