import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";
import VendorList from "./components/VendorList";

const eventCategories = [
    { id: 1, name: "🎉 Weddings", description: "Expert planners, decorators, and caterers to make your special day unforgettable." },
    { id: 2, name: "🏢 Corporate Events", description: "Venues, professional speakers, and event coordinators for business conferences and networking." },
    { id: 3, name: "🎭 Entertainment Shows", description: "DJs, musicians, and performers to add life to concerts, parties, and celebrations." },
    { id: 4, name: "🍽 Catering Services", description: "Top-rated caterers offering customized menus for weddings, parties, and corporate gatherings." }
];

function App() {
    const [vendors, setVendors] = useState([]);
    const [searchTerm, setSearchTerm] = useState("");
    const [error, setError] = useState(null);

    useEffect(() => {
        axios.get("http://localhost:5000/vendors")
            .then(response => {
                console.log("Fetched Vendors:", response.data);
                setVendors(response.data);
            })
            .catch(error => {
                console.error("Error fetching vendors:", error);
                setError("Failed to load vendors. Please try again.");
            });
    }, []);

    const filteredVendors = vendors.filter(vendor =>
        vendor?.name?.toLowerCase().includes(searchTerm?.toLowerCase())
    );

    return (
        <div className="app">
            {/* Navigation Bar */}
            <nav className="navbar">
                <h1>Local Event Planner</h1>
                <div className="nav-links">
                    <a href="#">Home</a>
                    <a href="#">Vendors</a>
                    <a href="#">Contact</a>
                </div>
            </nav>

            {/* Search Bar */}
            <section className="search-container">
                <input
                    type="text"
                    placeholder="Search for vendors..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="search-bar"
                />
            </section>

            {error && <p className="error-message">{error}</p>}

            <section className="description-box">
                <h2>Welcome to Local Event Planner</h2>
                <p>
                    Simplify your event planning experience! Explore our platform to connect with
                    top-rated vendors, get expert recommendations, and organize flawless events.
                    From weddings to corporate meetings, we've got you covered!
                </p>
            </section>

            {/* Featured Event Categories */}
            <section className="event-categories">
                <h2>Popular Event Categories</h2>
                <div className="categories-container">
                    {eventCategories.map(category => (
                        <div key={category.id} className="category-card">
                            <h3>{category.name}</h3>
                            <p>{category.description}</p>
                        </div>
                    ))}
                </div>
            </section>

            <main>
                <h2 className="section-heading">Our Services</h2>
                <VendorList vendors={filteredVendors} />
            </main>

            <footer className="app-footer">
                <p>&copy; 2025 Local Event Planner. All Rights Reserved.</p>
            </footer>
        </div>
    );
}

export default App;
