import React, { useState } from "react";
import axios from "axios";
import "./App.css";
import VendorList from "./components/VendorList";
import Testimonials from "./components/Testimonials"; // Importing Testimonials Component

const eventCategories = [
    { id: 1, name: "🎉 Weddings", description: "Expert planners, decorators, and caterers to make your special day unforgettable." },
    { id: 2, name: "🏢 Corporate Events", description: "Venues, professional speakers, and event coordinators for business conferences and networking." },

    { id: 4, name: "🍽 Catering Services", description: "Top-rated caterers offering customized menus for weddings, parties, and corporate gatherings." }
];

function App() {
    const [vendors, setVendors] = useState([]);
    const [searchTerm, setSearchTerm] = useState("");
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(false);

    const handleSearch = async () => {
        if (!searchTerm.trim()) {
            setVendors([]); // Clear vendors if search is empty
            return;
        }

        setLoading(true);
        try {
            const response = await axios.get(`http://localhost:5000/vendors?search=${searchTerm}`);
            setVendors(response.data);
        } catch (error) {
            console.error("Error fetching vendors:", error);
            setError("Failed to load vendors. Please try again.");
            setVendors([]);
        }
        setLoading(false);
    };

    return (
        <div className="app">
            {/* Navigation Bar */}
            <nav className="navbar">
                <h1>Local Event Planner</h1>
                <div className="nav-links">
                    <a href="#">Home</a>
                    <a href="#">Vendors</a>
                    <a href="#">Contact</a>
                </div>
            </nav>

            {/* Search Bar */}
            <section className="search-container">
                <input
                    type="text"
                    placeholder="Search for vendors..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="search-bar"
                />
                <button onClick={handleSearch} className="search-button">🔍 Search</button>
            </section>

            {/* Error Message */}
            {error && <p className="error-message">{error}</p>}

            {/* Loading Message */}
            {loading && <p className="loading-message">Searching for vendors...</p>}

            {/* Description Box */}
            <section className="description-card">
                <h2>Welcome to Local Event Planner</h2>
                <p>
                    Simplify your event planning experience! Explore our platform to connect with
                    top-rated vendors, get expert recommendations, and organize flawless events.
                    From weddings to corporate meetings, we've got you covered!
                </p>
            </section>

            {/* Featured Event Categories */}
            <section className="event-categories">
                <h2>Popular Event Categories</h2>
                <div className="categories-container">
                    {eventCategories.map(category => (
                        <div key={category.id} className="category-card">
                            <h3>{category.name}</h3>
                            <p>{category.description}</p>
                        </div>
                    ))}
                </div>
            </section>

            {/* Centered Search Results */}
            <main className="search-results-container">
                <h2 className="section-heading">Search Results</h2>
                {vendors.length > 0 ? (
                    <VendorList vendors={vendors} />
                ) : (
                    <p className="no-results">No matching vendors found.</p>
                )}
            </main>

            {/* Testimonials Section */}
            <Testimonials />

            <footer className="app-footer">
                <p>&copy; 2025 Local Event Planner. All Rights Reserved.</p>
            </footer>
        </div>
    );
}

export default App;
