import React, { useState } from "react";
import "./VendorList.css";

function VendorList({ vendors }) {
    const [selectedVendor, setSelectedVendor] = useState(null);
    const [showFilters, setShowFilters] = useState(false);

    const handleVendorClick = (vendor) => {
        setSelectedVendor(vendor);
        setShowFilters(true); // Display filters on click
    };

    return (
        <div className="vendor-list">
            <h2 className="section-heading">Search Results</h2>
            <div className="vendor-list-container">
                {vendors.map((vendor) => (
                    <div className="vendor-card" key={vendor.id} onClick={() => handleVendorClick(vendor)}>
                        <h3>{vendor.name}</h3>
                        <p><strong>Category:</strong> {vendor.category}</p>
                        <p><strong>Location:</strong> {vendor.location}</p>
                        <p><strong>Rating:</strong> ⭐ {vendor.rating}</p>
                        <p><strong>Services:</strong> {vendor.services.join(", ")}</p>
                        <p><strong>Rate Per Event:</strong> {vendor.ratePerEvent}</p>
                        <p><strong>Contact:</strong> {vendor.contact}</p>
                    </div>
                ))}
            </div>

            {/* Filter Panel (Shown on Vendor Click) */}
            {showFilters && (
                <div className="filter-panel">
                    <h3>Filter Vendors</h3>
                    <label>Rating:</label>
                    <select>
                        <option>All</option>
                        <option>⭐⭐⭐⭐⭐</option>
                        <option>⭐⭐⭐⭐</option>
                        <option>⭐⭐⭐</option>
                    </select>

                    <label>Price Range:</label>
                    <select>
                        <option>All</option>
                        <option>$2000 - $8000</option>
                        <option>$8000 - $15000</option>
                        <option>$15000+</option>
                    </select>

                    <button className="filter-button" onClick={() => setShowFilters(false)}>Close Filters</button>
                </div>
            )}
        </div>
    );
}

export default VendorList;
