import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";
import VendorList from "./components/VendorList";

function App() {
  const [vendors, setVendors] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:5000/vendors")
      .then((response) => setVendors(response.data))
      .catch((error) => console.error("Error fetching vendors:", error));
  }, []);

  return (
    <div className="app">
      <header className="app-header">
        <h1>Local Event Planner</h1>
      </header>
      <section className="description-box">
        <h2>Welcome to Local Event Planner</h2>
        <p>
          Plan your events effortlessly with our platform! Explore top vendors,
          from caterers to entertainers, and make your event unforgettable. Join
          us to transform your ideas into memorable experiences!
        </p>
      </section>
      <main>
        <h2 className="section-heading">Explore Our Features</h2>
        <div className="content-area">
          <div className="content-box">
            <h3>Search Vendors</h3>
            <p>Find the best vendors tailored for your event needs.</p>
          </div>
          <div className="content-box">
            <h3>Recommendations</h3>
            <p>
              Get personalized vendor recommendations based on your preferences.
            </p>
          </div>
          <div className="content-box">
            <h3>Plan Easily</h3>
            <p>Organize your event with our seamless tools and resources.</p>
          </div>
        </div>
        <VendorList vendors={vendors} />
      </main>
    </div>
  );
}

export default App;
